  height: 100px;
  background-color: #9ea5a9;
  border-radius: 50%;
}

.sidebar-profile img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.sidebar-profile p {
  width: 100%;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  text-align: center;
  color: #fff;
  margin-top: 16px;
}

.sidebar-menu {
  transition: all 5s ease-in-out;
}

.sidebar-menu p {
  color: #bfbfbf;
  margin-bottom: 16px;
}

.sidebar-menu ul {
  list-style: none;
  margin: 0;
  padding: 0;
}

.sidebar-menu ul li a {
  color: #fff;
  padding: 12px 14px;
  display: flex;
  align-items: center;
  gap: 16px;
  text-decoration: none;
}

.sidebar-menu ul li a.active,
.sidebar-menu ul li.active a {
  background-color: #1b5299;
  border-radius: 20px;
}

.sidebar-more {
  display: flex;
  align-items: center;
  justify-content: center;
  padding-bottom: 24px;
  margin-top: 16px;
}

.sidebar-more i {
