  transform: rotate(180deg);
}

@media (max-width: 1024px) {
  .sidebar-menu ul li a {
    font-size: 13px;
  }
}

.sidebar-fade-enter-from,
.sidebar-fade-leave-to {
  opacity: 0;
  transform: translateY(-20px);
}

.sidebar-fade-enter-active {
  transition: all 0.3s ease-out;
}

